INSERT INTO `news` VALUES ('50', '8', '档案管理员', '<p>岗位职责：</p>\r\n<p>1．负责按照公司档案管理制度对应归档的各类文件、证照、资料等进行收集、整理、建档、归档等工作；</p>\r\n<p>2．负责保证档案资料的完整和安全；</p>\r\n<p>3．遵守国家和公司的保密规定及有关规程；</p>\r\n<p>4．完成领导交办的其他工作。</p>\r\n<p>任职要求：</p>\r\n<p>1、全日制本科及以上学历，档案管理类相关专业,有档案管理员岗位证书者优先；</p>\r\n<p>2、从事档案管理工作3年以上；</p>\r\n<p>3、熟练使用办公软件，有专业资格证优先；</p>\r\n<p>4、工作认真负责，性格稳重有责任心；</p>\r\n<p>5、具备较强理解、学习能力，条理清晰、严谨踏实。</p>\r\n<p>联系电话：021-61507476  联系邮箱：<a href=\"mailto:tangdachun@shstzz.com\">tangdachun@shstzz.com</a></p>', '0', '2014-09-10', 'upfiles/CR-ft9qCANYpW');
